function useInView(threshold = 0.15) {
  const ref = useRef(null);
  const [visible, setVisible] = useState(false);
  useEffect(() => {
    const obs = new IntersectionObserver(([e]) => { if (e.isIntersecting) setVisible(true); }, { threshold });
    if (ref.current) obs.observe(ref.current);
    return () => obs.disconnect();
  }, []);
  return [ref, visible];
}

function SkillBar({ name, level, delay }) {
  const [ref, visible] = useInView();
  return (
    <div ref={ref} style={{ animationDelay: delay }} className={visible ? "fade-up" : "opacity-0"}>
      <div className="flex justify-between mb-1">
        <span style={{ fontFamily: "'DM Sans', sans-serif", color: "#c4d4f0", fontSize: "0.9rem" }}>{name}</span>
        <span style={{ color: "#60a5fa", fontWeight: 700, fontSize: "0.85rem" }}>{level}%</span>
      </div>
      <div style={{ background: "rgba(255,255,255,0.08)", borderRadius: 999, height: 8, overflow: "hidden" }}>
        <div
          style={{
            height: "100%",
            width: visible ? `${level}%` : "0%",
            background: "linear-gradient(90deg, #3b82f6, #a855f7, #ec4899)",
            borderRadius: 999,
            transition: "width 1.2s cubic-bezier(0.4,0,0.2,1)",
            transitionDelay: delay,
          }}
        />
      </div>
    </div>
  );
}

